@param {Args} args
@param {Ctx} ctx*/
async function tailwindToToddle (args, ctx) {
  const {tailwindToToddle} = await import("https://cdn.jsdelivr.net/npm/@toddledev/tailwind-parser@1.0.4/+esm")
  ctx.triggerActionEvent("data", tailwindToToddle(args.html))
}

