classCleanup.handler:
/**
 * @param {Args} args
 * @param {Ctx} ctx
 */
function classCleanup(args, ctx) {

let jsonString = `${args.nodes}`;

function replaceClasses(jsonStr) {
  let result = '';
  let index = 0;

  while (index < jsonStr.length) {
    let classesIndex = jsonStr.indexOf('"classes"', index);
    if (classesIndex === -1) {

      result += jsonStr.slice(index);
      break;
    } else {

      result += jsonStr.slice(index, classesIndex);
      index = classesIndex;

      let braceIndex = jsonStr.indexOf('{', index);
      if (braceIndex === -1) {
        throw new Error('Malformed JSON: Expected "{" after "classes"');
      }

      result += '"classes":{}';
      index = braceIndex + 1;

      let braceCount = 1;
      while (braceCount > 0 && index < jsonStr.length) {
        if (jsonStr[index] === '{') braceCount++;
        else if (jsonStr[index] === '}') braceCount--;
        index++;
      }

      if (braceCount !== 0) {
        throw new Error('Malformed JSON: Unmatched braces in "classes" object');
      }
    }
  }

  return result;
}

jsonString = replaceClasses(jsonString);

return jsonString

}

